mapper.update:
UPDATE TB_TRNS_HST
SET STS_CD = #{stsCd}
WHERE ACNT_NO = #{acntNo}
AND SEQ = ${seq};

UPDATE TB_TRNS_HST
SET STS_CD = #{stsCd}

    ,ACNT_BLNC = (SELECT ACNT_BLNC
    FROM TB_TRNS_HST
    WHERE ACNT_NO = #{acntNo}
    AND SEQ = (SELECT COALESCE(MAX(SEQ),0) FROM TB_TRNS_HST WHERE ACNT_NO = #{acntNo} AND STS_CD = '1')
    )

WHERE ACNT_NO = #{acntNo}
AND SEQ = ${seq};

mapper.select:
SELECT COALESCE(MAX(SEQ),0)
FROM TB_TRNS_HST 
WHERE ACNT_NO = #{acntNo}
